import './App.css';
import React,{Component} from 'react' 
import axios from 'axios'
class Users extends Component
{
  constructor(props)
  {
    super(props)
    this.state = {details: {}}
  }



  mouseOver(allUsers)
  {
    this.setState({details: [
      "Street": allUsers.address.street ,
        "City": allUsers.address.city ,
        "ZipCode": allUsers.address.zipcode
    ]})
    // <div>
    //   Street: {this.props.allUsers.address.street} <br/>
    //   City: {this.props.allUsers.address.city} <br/>
    //   Zip Code: {this.props.allUsers.address.zipcode} <br/>
    // </div>
    // // console.log(allUsers.name)
  }

  render()
  {
    // console.log(this.props.isRed)
    let borderColor = ""
    if(this.props.isRed === true)
    {
      borderColor = "App-Border-User-Red"
    }
    else{
      borderColor = "App-Border-User-Green"
    }


    return(

      <div className = {borderColor}>
        Id: {this.props.allUsers.id}<br/>
        Name: <input type = "text" placeholder = {this.props.allUsers.name} /><br/>
        Email: <input type = "text" placeholder = {this.props.allUsers.email} /><br/><br/>
        <input type = "button" value= "Other Data" onMouseOver = {() => this.mouseOver(this.props.allUsers)} />
        {this.state.details}
        <input type = "button" value= "Update" />
        <input type = "button" value= "Delete" />
      </div>

    )
  }
}

export default Users;
