const MainReducer = (state = { todos : [] , posts: []}, action) =>
{
    switch(action.type)
    {
        case 'ShowDetails':
            return {...state, todos : state.todos + action.payload.todos, posts : state.posts + action.payload.posts}

        default:
            return state;
    }
}


export default MainReducer;
